let onlongtouch = false;
let timer = false;
let duration = 5000;

function touchStart(){
  if (!timer) {
    timer = setTimeout(onlongtouch, duration);
  }
}

function touchEnd(){
  if (timer) {
    clearTimeout(timer)
    timer = false;
  }
}

onlongtouch = function(){
  document.querySelector("#brackets").innerHTML = "Hello";
}

document.addEventListener("DOMContentLoaded", function(){
  document.querySelector("#area").addEventListener("touchstart", touchStart);
  document.querySelector("#area").addEventListener("touchend", touchEnd);
})
