let onlongtouch = false;
let timer = false;
let duration = 5000;
let id = "#brackets";

function touchStart(){
  if (!timer) {
    timer = setTimeout(onlongtouch, duration);
  }
}

function touchEnd(){
  if (timer) {
    clearTimeout(timer)
    timer = false;
  }
}

onlongtouch = function(){
  document.querySelector(id).innerHTML = "Hello";
}

document.addEventListener("DOMContentLoaded", function(){
  document.querySelector(id).addEventListener("touchstart", touchStart);
  document.querySelector(id).addEventListener("touchend", touchEnd);
})
