let onlongtouch = false;
let timer = false;
let duration = 5000;
let id = "#brackets";

function touchStart(){
  if (!timer) {
    timer = setTimeout(onlongtouch, duration);
  }
}

function touchEnd(){
  if (timer) {
    clearTimeout(timer)
    timer = false;
  }
}

onlongtouch = function(){
  //document.querySelector(#brackets").innerHTML = "Hello";
  document.getElementById('HiddenScreen').style.zIndex = '-1';
}

document.addEventListener("DOMContentLoaded", function(){
  document.querySelector("#brackets").addEventListener("touchstart", touchStart);
  document.querySelector("#brackets").addEventListener("touchend", touchEnd);
})
